import React from 'react'
import { UilEllipsisV } from '@iconscout/react-unicons'
import More from './More'

function TopBtns(props) {

  const [showMore, setShowMore] = React.useState(false)

    const cities = [
        {
            id: 1,
            title: 'London'
        },{
            id: 2,
            title: 'Sydney'
        },{
            id: 3,
            title: 'Tokyo'
        },{
            id: 4,
            title: 'Toronto'
        },{
            id: 5,
            title: 'Paris'
        },
    ]

  return (<>
    <More showMore={showMore}/>
    <div className='top-btns flex items-center justify-around my-6'>
        {cities.map((city, id) => {
            return <button key={id} 
            className='text-white text-lg ffont-medium'
            onClick={() => props.setQuery({q: city.title})}>{city.title}</button>
        })}
        <UilEllipsisV className="text-white cursor-pointer" onClick={() => setShowMore((oldMore) => !oldMore)}/>
    </div>
    </>
  )
}

export default TopBtns
