import React from 'react';
import UilReact from '@iconscout/react-unicons/icons/uil-react'
import TopBtns from './components/TopBtns';
import Inputs from './components/Inputs';
import TimeaAndLocation from './components/TimeaAndLocation';
import TempDetails from './components/TempDetails';
import Forecast from './components/Forecast';
import getFormattedData from './services/WeatherServices';

function App() {

  const [query, setQuery] = React.useState({q: 'berlin'})
  const [unit, setUnit] = React.useState('metric')
  const [weather, setWeather] = React.useState(null)

  React.useEffect(() => {
    async function fetchWeather() {
      const data =  await getFormattedData({q: 'london'});
      console.log(data)
    }
    fetchWeather()
  },[])

  return (
    <div className="App mx-auto max-w-screen-md mt-4 my-4 py-5 px-32 bg-gradient-to-br from-cyan-700 to-blue-700 h-fit shadow-xl shadow-gray-400">
      <TopBtns/>
      <Inputs/>
      <TimeaAndLocation/>
      <TempDetails/>
      <Forecast title="hourly forecast"/>
      <Forecast title="daily forecast"/>
    </div>
  );
}

export default App;
