import React from 'react'
import { UilSearch,  UilLocationPoint } from '@iconscout/react-unicons'
import { toast } from 'react-toastify'

function Inputs(props) {
  const [city, setCity] = React.useState("")

  function handleSearchClick() {
    if(city !== "") {
      props.setQuery({q: city})
    }
  }

  function handleLocation() {
    if(navigator.geolocation){
      toast.info('Fetching your location')
      navigator.geolocation.getCurrentPosition((position) => {
        toast.succes
        let lat = position.coords.latitude
        let lon = position.coords.longitude
        props.setQuery({lat, lon})
      })
    }
  }

  function handleUnitChange(e) {
    const selectedUnit = e.target.name
    if (props.units !== selectedUnit) {
      props.setUnit(selectedUnit)
    }
  }

  return (
    <div className='flex flex-row justify-center my-6'>
        <div className="flex flex-row w-3/4 items-center justify-center space-x-4">
            <input type="text"
            placeholder='Search city'
             className='text-xl font-light p-2 w-full shadow-xl focus:outline-none capitalize placeholder:lowercase'
             onChange={(e) => setCity(e.target.value)} />
             <UilSearch size={25} 
                className="text-white cursor-pointer transition ease-out hover:scale-125"
                onClick={handleSearchClick} />
             <UilLocationPoint size={25} 
                className="text-white cursor-pointer transition ease-out hover:scale-125"
                onClick={handleLocation}/>
        </div>
        <div className='flex flex-row w-1/4 items-center justify-center'>
            <button name='matric' className='text-xl text-white font-light hover:scale-125 transition ease-out' onClick={handleUnitChange}>â„ƒ</button>
            <p className="texr-xl text-white mx-1">|</p>
            <button name='inperial' className='text-xl text-white font-light hover:scale-125 transition ease-out' onClick={handleUnitChange}>F</button>
        </div>
    </div>
  )
}

export default Inputs
