import React from 'react'

function TopBtns(props) {

    function handleSetQuery(params) {
        props.setQuery({q: ''})
    }

    const cities = [
        {
            id: 1,
            title: 'London'
        },{
            id: 2,
            title: 'sydney'
        },{
            id: 3,
            title: 'Tokyo'
        },{
            id: 4,
            title: 'Toronto'
        },{
            id: 5,
            title: 'Paris'
        },
    ]

  return (
    <div className='top-btns flex items-center justify-around my-6'>
        {cities.map((city, id) => {
            return <button key={id} 
            className='text-white text-lg ffont-medium'
            onClick={handleSetQuery}>{city.title}</button>
        })}
    </div>
  )
}

export default TopBtns
