import React from 'react'
import { UilSlidersVAlt } from '@iconscout/react-unicons'

function TopBtns(props) {

    const [isPending, startTransition] = React.useTransition()

    function openModal() {
        props.setShowMore(true)
    }

    React.useEffect(() => {
            if (props.showMore) {
                document.body.style.overflow = "hidden";
            } else {
                document.body.style.overflow = "auto";
            }
    })

    const cities = [
        {
            id: 1,
            title: 'London',
        },{
            id: 2,
            title: 'Sydney',
        },{
            id: 3,
            title: 'Tokyo',
        },{
            id: 4,
            title: 'Paris',
        },
    ]

  return (<>
    <div className='top-btns flex items-center justify-around my-6'>
        {cities.map((city, id) => {
            return <button key={id} 
            className='text-white text-lg font-medium'
            onClick={() => props.setQuery({q: city.title})}>{city.title}</button>
        })}
        <UilSlidersVAlt className="text-white cursor-pointer" onClick={openModal}/>
    </div>
    </>
  )
}

export default TopBtns
