import React from 'react'
import { UilEllipsisV } from '@iconscout/react-unicons'

function TopBtns(props) {

    const cities = [
        {
            id: 1,
            title: 'London',
            icon: './big-ben.ico'
        },{
            id: 2,
            title: 'Sydney',
            icon: './eiffel-tower.ico'
        },{
            id: 3,
            title: 'Tokyo',
            icon: './big-ben.svg'
        },{
            id: 4,
            title: 'Toronto',
            icon: './big-ben.svg'
        },{
            id: 5,
            title: 'Paris',
            icon: './big-ben.svg'
        },
    ]

  return (<>
    <div className='top-btns flex items-center justify-around my-6'>
        {cities.map((city, id) => {
            return <button key={id} 
            className='text-white text-lg ffont-medium'
            onClick={() => props.setQuery({q: city.title})}>
                <img src={city.icon} alt="" className='w-3/4' />
            </button>
        })}
        <UilEllipsisV className="text-white cursor-pointer" onClick={() => props.setShowMore(true)}/>
    </div>
    </>
  )
}

export default TopBtns
