import './App.css';
import React from 'react';
import TopBtns from './components/TopBtns';
import Inputs from './components/Inputs';
import TimeaAndLocation from './components/TimeaAndLocation';
import TempDetails from './components/TempDetails';
import Forecast from './components/Forecast';
import getFormattedData from './services/WeatherServices';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import More from './components/More';


function App() {

  const [location, setLocation] = React.useState("")
  const currentUserCity = localStorage.getItem("usercity");
  const [query, setQuery] = React.useState({q: currentUserCity ? currentUserCity : 'berlin' })
  const [units, setUnit] = React.useState('metric')
  const [weather, setWeather] = React.useState(null)
  const [showMore, setShowMore] = React.useState(false)

  // if(currentUserCity !== "") {
  //   setQuery({q: currentUserCity})
  // }
  console.log(currentUserCity, query.q);

  React.useEffect(() => {
    (async function () {
      const msg = query.q ? query.q : 'current location'
      toast.info('Fetching weather for ' + msg)
      await getFormattedData({...query, units}).then((data) => {
        toast.success(`Successfully fetched weather for ${data.name}, ${data.country}`)
        setWeather(data)
      })
    })()
  },[query, units])

  function formatBg() {
    const thresold = units === 'metric'? 20 : 60
    if (!weather || weather.temp <= thresold) {
      return 'from-cyan-700 to-blue-700'
    }else{
      return 'from-yellow-700 to-orange-700'
    }
  }

  function handleCloseMore() {
    setShowMore(false)
  }

  function settingUserLocation() {
    localStorage.setItem("usercity", location);
    if(currentUserCity){
      console.log(currentUserCity);
      setQuery({q: currentUserCity})
      console.log(query)
    }
  }
  console.log(currentUserCity, query.q);

  return (<>
    {showMore && <More showMore={showMore} 
      handleCloseMore={handleCloseMore} 
      setLocation={setLocation}
      settingUserLocation={settingUserLocation}/>}
    <div className={`App container mx-auto max-w-screen-md mt-4 my-4 py-5 px-32 bg-gradient-to-br ${formatBg()} h-fit shadow-xl shadow-gray-400`}>
      <TopBtns setQuery={setQuery} setShowMore={setShowMore}/>
      <Inputs setQuery={setQuery} units={units} setUnit={setUnit}/>
      {weather && (
        <>
          <TimeaAndLocation weather={weather}/>
          <TempDetails weather={weather}/>
          <Forecast title="hourly forecast" items={weather.hourly}/>
          <Forecast title="daily forecast" items={weather.daily}/>
        </>
      )}
      <ToastContainer autoClose={5000} theme='colored' newestOnTop={true}/>
    </div>
    </>
  );
}

export default App;
